import{_ as e,o,c as r,d as t}from"./app.3398e39f.js";var i="/assets/blogImg/ckxt0.jpg",a="/assets/blogImg/ckxt1.jpg",s="/assets/blogImg/ckxt2.jpg",d="/assets/blogImg/ckxt3.jpg",n="/assets/blogImg/ckxt4.jpg";const p={},l=t('<h2 id="date-2012-12-18-23-23" tabindex="-1"><a class="header-anchor" href="#date-2012-12-18-23-23" aria-hidden="true">#</a> date: 2012-12-18 23:23</h2><p><img src="'+i+'" alt="\u523A\u5BA2\u4FE1\u6761-\u5144\u5F1F\u4F1A\u6D77\u62A5"></p><blockquote><p>Nothing is true, everything is permitted.\u4E07\u7269\u7686\u865A\uFF0C\u4E07\u4E8B\u7686\u5141\u3002</p></blockquote><p><em>\u2014\u2014 \u300A\u523A\u5BA2\u4FE1\u6761 2\u300B\u300A\u523A\u5BA2\u4FE1\u6761\uFF1A\u5144\u5F1F\u4F1A\u300B\u300A\u523A\u5BA2\u4FE1\u6761\uFF1A\u542F\u793A\u5F55\u300B-Ezio \u4E09\u90E8\u66F2</em></p><p>\u5B9E\u4E60\u56DE\u6765\u540E\uFF0C\u5728\u670B\u53CB\u7684\u63A8\u8350\u4E0B\u73A9\u4E86\u523A\u5BA2\u4FE1\u6761\u8FD9\u6B3E\u6E38\u620F\uFF0C\u611F\u89E6\u633A\u6DF1\uFF0C\u540E\u6765\u60F3\u4E00\u5B9A\u8981\u4E3A\u5B83\u5199\u70B9\u6587\u5B57\u3002\u95ED\u773C\u4E00\u60F3\uFF0C\u8FD8\u662F\u90A3\u53E5\u8BDD\u8426\u56DE\u8111\u4E2D\uFF1A\u6211\u4EEC\u4E3A\u4E86\u670D\u4F8D\u5149\u660E\u800C\u8015\u8018\u4E8E\u9ED1\u6697\u2026\u2026\u4E07\u7269\u7686\u865A\uFF0C\u4E07\u4E8B\u7686\u5141\u3002\u5374\u4E0D\u77E5\u9053\u8FD9\u53E5\u8BDD\u8BE5\u600E\u4E48\u7FFB\u8BD1\uFF0C\u88AB\u6E38\u620F\u5BD3\u610F\u4E3A\u201C\u4FE1\u6761\u201D\u7684\u8BDD\u8BED\uFF0C\u76F8\u5BF9\u4E8E\u5B83\u7684\u9898\u6750\u6240\u5305\u542B\u7684\u6DF1\u5EA6\uFF0C\u597D\u50CF\u4EFB\u4F55\u7FFB\u8BD1\u90FD\u4F1A\u5E26\u6765\u504F\u5DEE\u3002\u5F88\u96BE\u7528\u51E0\u53E5\u8BDD\u63CF\u8FF0\u6E05\u695A\u8FD9\u90E8\u4F5C\u54C1\uFF0C\u5B83\u5C55\u73B0\u4E86\u5B8C\u5168\u4E0D\u540C\u7684\u4E16\u754C\u4E0E\u4EF7\u503C\uFF0C\u8FC7\u53BB\u4E0E\u73B0\u5728\uFF0C\u751F\u5B58\u4E0E\u6B7B\u4EA1\uFF0C\u4EC7\u6068\u4E0E\u5BBD\u5BB9\uFF0C\u8FD9\u4E9B\u5BBD\u6CDB\u5F18\u6BC5\u7684\u5BF9\u7ACB\u8BCD\u76F8\u4E92\u4EA4\u7EC7\uFF0C\u5728\u5200\u5203\u4E0E\u9C9C\u8840\u4E2D\u91CD\u83B7\u65B0\u91CA\uFF0C\u6D53\u7F29\u4E8E\u4E3B\u4EBA\u516C Ezio \u7684\u4E00\u751F\u3002</p><h2 id="\u865A" tabindex="-1"><a class="header-anchor" href="#\u865A" aria-hidden="true">#</a> <strong>\u865A</strong></h2><p>Ezio \u8BDE\u751F\u4E8E\u6587\u827A\u590D\u5174\u65F6\u671F\u7684\u4F5B\u7F57\u4F26\u8428\uFF0C\u4F5C\u4E3A\u94F6\u884C\u5BB6\u7684\u5BCC\u4E8C\u4EE3\uFF0C\u6BCF\u65E5\u5C31\u662F\u6E38\u624B\u597D\u95F2\u6CBE\u82B1\u60F9\u8349\u3002\u8FD8\u8BB0\u5F97\u6E38\u620F\u7684\u7B2C\u4E00\u4E2A\u4EFB\u52A1\u5C31\u662F\u53BB\u6253\u67B6\uFF0C\u7136\u540E\u722C\u4E0A\u9AD8\u697C\u8DDF\u59D1\u5A18\u5E7D\u4F1A\u3002\u751F\u6D3B\u6CE2\u6F9C\u4E0D\u60CA\uFF0C\u57FA\u8C03\u5982\u7FE1\u7FE0\u822C\u5E73\u548C\uFF0C\u84DD\u5929\u767D\u4E91\u4E4B\u4E0B\u7684\u9C9C\u82B1\u4E4B\u57CE\u7ED9\u4E88\u4E86 Ezio \u592A\u591A\u7EA8\u7ED4\u5B50\u5F1F\u7684\u6C14\u606F\u3002\u800C\u81F3\u4E8E\u540E\u6765\uFF0C\u5F53\u4ED6\u6BCF\u6B21\u7684\u4EFB\u52A1\u90FD\u8981\u624B\u8D77\u5200\u843D\u5FEB\u610F\u6069\u4EC7\uFF0C\u7136\u540E\u5B89\u629A\u4EA1\u8005\u7684\u7075\u9B42\uFF0C\u5F53\u4ED6\u5BF9\u6DF1\u7231\u7684 Sophia \u7684\u8BF7\u6C42\u65E0\u5948\u9053\u51FA\u5BF9\u4E0D\u8D77\u6211\u7684\u65F6\u95F4\u5F88\u5B9D\u8D35\uFF0C\u5F53\u4ED6\u5728\u5F02\u4E61\u7684\u6DF1\u591C\u7ED9\u59B9\u59B9 Claudia \u6EE1\u6000\u6DF1\u60C5\u5730\u5199\u4FE1\u65F6\uFF0C\u4F60\u624D\u4F1A\u53D1\u73B0\uFF0C\u4ED6\u52A8\u8361\u7684\u5185\u5FC3\u6DF1\u5904\uFF0C\u81EA\u6709\u4E00\u4E2A\u4E0D\u66FE\u60CA\u6270\u7684\u4E16\u754C\uFF0C\u4E00\u4E2A\u4E0D\u66FE\u60CA\u6270\u7684\u7FE1\u51B7\u7FE0\u3002\u90A3\u4E9B\u901D\u53BB\u7684\u65E5\u5B50\uFF0C\u5728\u6E38\u620F\u5F00\u5934\u7565\u663E\u65E0\u804A\u7684\u60C5\u666F\uFF0C\u6070\u5982\u4E00\u4E2A\u7B80\u6734\u7684\u522B\u9488\uFF0C\u56FA\u5B9A\u5728\u5FC3\u4E2D\u6700\u7EAF\u7CB9\u7684\u89D2\u843D\u3002</p><p><img src="'+a+'" alt="\u5954\u6CE2\u5230\u5A01\u5C3C\u65AF\u7684Ezio"></p><p>\u4EE5\u81F3\u4E8E\u8BF4\uFF0C\u4F55\u4EE5\u4E3A\u6B63\u4E49\uFF0C\u771F\u7406\uFF0C\u4FE1\u4EF0\uFF0C\u81EA\u7531\uFF1F\u8FD9\u90FD\u662F\u4E9B\u591A\u4E48\u7F8E\u597D\u7684\u8BCD\u8BED\uFF0C\u7136\u800C\u90FD\u4E0D\u662F Ezio \u6700\u7EC8\u60F3\u8981\u7684\uFF0C\u53C8\u6070\u597D\u662F\u4ED6\uFF0C\u89C1\u8FC7\u4E86\u592A\u591A\u4E3A\u4E86\u8FD9\u4E9B\u7F8E\u597D\u800C\u75AF\u72C2\u8FFD\u9010\uFF0C\u6700\u7EC8\u5760\u4E0B\u6DF1\u6E0A\u7684\u4EBA\u3002\u597D\u5947\u5BB3\u6B7B\u732B\uFF0C\u4EBA\u53C8\u4F55\u5C1D\u4E0D\u662F\uFF1F\u56E0\u6B64\uFF0C\u521B\u4E16\u7684\u795E\u8BA9\u4EBA\u95F4\u6709\u4E86\u6B7B\u4EA1\u3002\u523A\u5BA2\u4ECE\u5929\u800C\u964D\uFF0C\u5E3D\u6A90\u906E\u4F4F\u4ED6\u7684\u7709\u5B87\uFF0C\u548C\u5E73\u9E3D\u8FD8\u4E0D\u77E5\u6653\u4E0B\u4E00\u523B\u7684\u614C\u4E71\uFF0C\u98CE\u8D77\u98CE\u6B62\u95F4\uFF0C\u4F18\u96C5\u7684\u8896\u5203\u5DF2\u5C06\u8FF7\u4E71\u7684\u9C9C\u7EA2\u7EFD\u653E\u3002</p><p>Ezio \u66F4\u50CF\u4E00\u4E2A\u5E03\u9053\u8005\uFF0C\u5C06\u201C\u865A\u201D\u7684\u4FE1\u6761\u7528\u6B7B\u4EA1\u7684\u65B9\u5F0F\u4F20\u8C15\u3002\u800C\u4E8B\u7269\u7684\u771F\u5B9E\u4E0E\u5426\uFF0C\u672C\u6765\u5C31\u65E0\u6240\u8C13\u4EBA\u7684\u597D\u574F\u3002\u670B\u53CB Leonardo \u662F\u865A\u7684\uFF0C\u4ED6\u82B1\u4E86\u5F88\u591A\u5FC3\u91CC\u53D1\u660E\u6218\u4E89\u673A\u5668\uFF0C\u4E0B\u4E00\u523B\u5C31\u60F3\u6467\u6BC1\u5B83\u4EEC\uFF1B\u53D4\u53D4 Mario \u662F\u865A\u7684\uFF0C\u4E00\u4E2A\u8D28\u6734\u679C\u6562\u7684\u9886\u8DEF\u4EBA\uFF0C\u5728\u5E84\u56ED\u9677\u843D\u65F6\u4FBF\u864E\u843D\u5E73\u9633\uFF1BNiccolo \u4E5F\u662F\u865A\u7684\uFF0C\u4E00\u4E2A\u6EE1\u8179\u7ECF\u7EB6\u5162\u5162\u4E1A\u4E1A\u7684\u523A\u5BA2\u9996\u9886\uFF0C\u5374\u4E00\u5EA6\u88AB\u5F53\u4F5C\u5185\u5978\u3002\u8FDE Ezio \u672C\u4EBA\u4E5F\u662F\u865A\u7684\uFF0C\u4ECE\u4F5B\u7F57\u4F26\u8428\u7684\u8D77\u6B65\uFF0C\u5230\u5A01\u5C3C\u65AF\u7684\u8FFD\u5BFB\uFF0C\u518D\u5230\u7F57\u9A6C\u7684\u590D\u5174\uFF0C\u6700\u540E\u5230\u541B\u58EB\u5766\u4E01\u5821\u7684\u8FDF\u66AE\uFF0C\u5176\u5B9E Ezio \u6700\u7EC8\u624D\u8BA4\u8BC6\u5230\uFF0C\u81EA\u5DF1\u4E0D\u4E5F\u662F\u4E3A\u6240\u8C13\u7684\u590D\u4EC7\u4E0E\u8363\u8000\u52AA\u529B\u4E86\u4E00\u8F88\u5B50\u5417\uFF1F\u539F\u6765\u90A3\u4E2A\u6700\u5148\u5760\u5165\u6DF1\u6E0A\u7684\u4EBA\uFF0C\u6B63\u662F Ezio \u81EA\u5DF1\u3002</p><p><img src="'+s+'" alt="\u523A\u5BA2\u4ECE\u5929\u800C\u964D"></p><p>\u56E0\u6B64\uFF0CEzio \u6700\u540E\u5728\u548C\u5BB3\u6B7B\u4E86\u7236\u5144\u7684\u6559\u7687 Rodrigo \u5BF9\u5CD9\u65F6\uFF0C\u7559\u4E0B\u8FD9\u53E5\u4FE1\u6761\u800C\u653E\u8FC7\u4E86\u4ED6\uFF08\u6700\u7EC8\u53CD\u88AB\u513F\u5B50 Cesare \u6BD2\u6B7B\uFF09\u3002\u8FD9\u4E00\u523B\uFF0C\u4E0D\u77E5\u624B\u67D3\u592A\u591A\u9C9C\u8840\u7684\u523A\u5BA2\u5927\u5E08\u662F\u5426\u60F3\u5230\u4E86\u591A\u5E74\u524D\u7684\u4E00\u5C18\u4E0D\u67D3\u7684\u4F5B\u7F57\u4F26\u8428\u3002\u6289\u62E9\u4E0E\u53D8\u5366\uFF0C\u8FC7\u4E00\u5206\u949F\u5C31\u4E0D\u80FD\u518D\u56DE\u5934\u3002Ezio \u7684\u8EAB\u540E\u8FD9\u5EA7\u8F89\u714C\u7684\u57CE\u5E02\uFF0C\u539F\u672C\u5C31\u662F\u4ED6\u60F3\u7EC8\u7ED3\u90AA\u6076\uFF0C\u83B7\u5F97\u8363\u8000\u7684\u76EE\u7684\u5730\u3002\u5982\u4ECA\uFF0C\u57CE\u5E02\u66F4\u52A0\u8F89\u714C\uFF0C\u4EE5\u5F80\u7684\u65E5\u5B50\uFF0C\u7231\u4EBA\uFF0C\u81EA\u7531\uFF0C\u5374\u4E00\u5E76\u5931\u53BB\u4E86\u8E2A\u5F71\u3002\u76F8\u5BF9\u4E8E\u5F53\u4E0B\uFF0C\u4ED6\u77E5\u9053\u4E00\u5207\u4E8E\u4E8B\u65E0\u8865\uFF0C\u53CD\u800C\u6709\u4E00\u79CD\u7E41\u82B1\u843D\u5C3D\uFF0C\u6084\u7136\u5165\u571F\u7684\u968F\u6027\u3002\u4ED6\u8D6B\u7136\u53D1\u73B0 Nothing is true \u539F\u6765\u4E0D\u662F\u5BF9\u522B\u4EBA\u7684\u544A\u8BEB\uFF0C\u800C\u662F\u5BF9\u81EA\u5DF1\u5185\u5FC3\u6DF1\u5904\u6700\u65E0\u5948\u7684\u72EC\u767D\u3002</p><h2 id="\u5141" tabindex="-1"><a class="header-anchor" href="#\u5141" aria-hidden="true">#</a> <strong>\u5141</strong></h2><p>\u4EFB\u4F55\u4E00\u4E2A\u523A\u5BA2\uFF0C\u52A0\u5165 brotherhood \u65F6\u90FD\u8981\u5B8C\u6210\u4E00\u4E2A\u4EEA\u5F0F\u3002\u4ED6\u4EEC\u4ECE\u57CE\u5E02\u6700\u9AD8\u7684\u5854\u5C16\u4E0A\u4FEF\u77B0\u5168\u57CE\uFF0C\u7136\u540E\u5411\u7740\u5854\u5E95\u7684\u8349\u5806\u9AD8\u9AD8\u8DC3\u4E0B\u3002\u4ED6\u4EEC\u5728\u8DC3\u4E0B\u7684\u90A3\u4E00\u523B\u4F38\u5C55\u624B\u81C2\uFF0C\u4E00\u79CD\u8FD1\u4E4E\u75AF\u72C2\u7684\u81EA\u7531\u547C\u5578\u800C\u6765\uFF0C\u5C06\u70E6\u607C\u548C\u6050\u60E7\u629B\u8BF8\u8111\u540E\uFF0C\u4F60\u5F88\u5BB9\u6613\u8054\u60F3\u5230\u9E70\u7684\u610F\u8C61\u3002\u6211\u4EEC\u603B\u662F\u53EF\u4EE5\u5C06\u9053\u7406\u793A\u4E8E\u522B\u4EBA\uFF0C\u5374\u65E0\u6CD5\u8BF4\u670D\u81EA\u5DF1\u3002\u8FD9\u79CD\u4EEA\u5F0F\u65E0\u7591\u662F\u5BF9\u81EA\u8EAB\u7684\u52C7\u6562\u4E0E\u81EA\u7531\u6700\u597D\u7684\u8BE0\u91CA\uFF0C\u523A\u5BA2\u4EEC\u4EE5\u8FD9\u6837\u7684\u65B9\u5F0F\u5411\u57CE\u5E02\u5BA3\u544A\uFF0C\u4ED6\u4EEC\u7684\u884C\u4E3A\u51C6\u5219\u79C9\u627F\u4FE1\u4EF0\uFF0C\u5141\u8BB8\u4ED6\u4EEC\u5728\u6CD5\u5F8B\u548C\u9053\u5FB7\u4E2D\u638C\u63E1\u90AA\u6076\u8005\u7684\u751F\u6740\u5927\u6743\u3002\u4ED6\u4EEC\u79F0\u4E4B\u4E3A\u201C\u4FE1\u4EF0\u4E4B\u8DC3\u201D\u3002</p><p><img src="'+d+'" alt="\u4FE1\u4EF0\u4E4B\u8DC3"></p><p>\u4ED6\u4EEC\u5728\u5854\u5C16\u98D8\u98DE\u6D88\u5931\uFF0C\u57CE\u5E02\u7684\u98CE\u666F\u60C5\u97F5\u5C3D\u6536\u773C\u5E95\uFF0C\u80CC\u540E\u662F\u865A\u65E0\u7684\u9AD8\u5854\uFF0C\u706B\u7130\uFF0C\u8363\u8000\uFF0C\u524D\u65B9\u662F\u88AB\u5141\u8BB8\u7684\u4F7F\u547D\u3002\u800C\u4E00\u4E2A\u4EBA\u7684\u597D\u4E0E\u574F\uFF0C\u751F\u4E0E\u6B7B\uFF0C\u65E0\u65F6\u4E0D\u5728\u62F7\u95EE\u7740 Ezio \u7684\u7075\u9B42\u3002\u662F\u7684\uFF0C\u4ED6\u4E5F\u523A\u6740\u8FC7\u597D\u4EBA\uFF0C\u800C\u4ED6\u66F4\u591A\u9009\u62E9\u4E86\u6C89\u9ED8\u4E0E\u5FD8\u5374\u3002\u5141\u8BB8\uFF0C\u5230\u5E95\u8C01\u5728\u5141\u8BB8\uFF1F\u5BFC\u5E08 Altair \u5728\u591A\u6B21\u523A\u6740\u540E\u7EC8\u4E8E\u6709\u89C9\u609F\u7684\u8BF4\uFF1A\u201C\u6211\u6240\u6740\u7684\u4EBA\u90FD\u5BF9\u6211\u8BF4\u4E86\u4E9B\u5947\u602A\u7684\u8BDD\u3002\u4ED6\u4EEC\u90FD\u6CA1\u6709\u540E\u6094\u3002\u5373\u4F7F\u5FEB\u6B7B\u4E86\uFF0C\u4ED6\u4EEC\u4E5F\u770B\u7740\u5BF9\u80DC\u5229\u5F88\u6709\u81EA\u4FE1\u3002\u6CD5\u5F8B\u4E0D\u662F\u6765\u81EA\u4E8E\u795E\uFF0C\u800C\u662F\u7406\u6027\u3002\u6211\u73B0\u5728\u660E\u767D\u4E86\u6211\u4EEC\u7684\u4FE1\u6761\u4E0D\u662F\u8981\u6C42\u6211\u4EEC\u53BB\u81EA\u7531\u3002\u662F\u8981\u6C42\u6211\u4EEC\u667A\u6167\u3002\u201D\u4E00\u4E2A\u5141\u5B57\uFF0C\u8981\u4E48\u6210\u4E3A\u523A\u6740\u540E\u4E0D\u89C9\u5F97\u7F6A\u6076\u7684\u501F\u53E3\uFF0C\u8981\u4E48\u6210\u4E3A\u8D85\u8D8A\u5047\u8C61\u7684\u5927\u667A\u6167\u3002</p><p>\u6E38\u620F\u60C5\u8282\u91CC\u6709\u4E00\u5E55\u6211\u5370\u8C61\u7279\u522B\u6DF1\uFF0C\u5E74\u8FC8\u7684 Ezio \u5C06\u5730\u4E0B\u57CE\u519B\u706B\u5E93\u7684\u9996\u9886\u523A\u6B7B\uFF0C\u5355\u819D\u8DEA\u5730\u62B1\u7740\u4ED6\u7684\u5C38\u4F53\uFF0C\u7F13\u7F13\u7684\u8BF4\uFF1A\u201C\u6740\u622E\u662F\u53EF\u803B\u7684\u2026\u201D\u3002\u4F60\u80FD\u5426\u60F3\u8C61\uFF0C\u8FD9\u662F\u4E00\u4E2A\u624B\u6CBE\u9C9C\u8840\uFF0C\u4EE5\u523A\u6740\u4E3A\u8363\u7684\u523A\u5BA2\u5927\u5E08\uFF0C\u5BF9\u7740\u521A\u88AB\u81EA\u5DF1\u523A\u6740\u7684\u5C06\u4EA1\u4EBA\u8BF4\u51FA\u7684\u4E00\u756A\u8BDD\u3002\u4EBA\u5230\u4E86\u6B7B\u795E\u964D\u4E34\u8FD9\u4E00\u6B65\uFF0C\u624D\u4F1A\u660E\u767D\u6240\u8C13\u662F\u975E\u6210\u8D25\u8F6C\u5934\u7A7A\uFF0C\u624D\u4F1A\u660E\u767D\u6743\u5229\u548C\u91D1\u94B1\u90FD\u4F1A\u5728\u4E0B\u4E00\u6B21\u7728\u773C\u4E2D\u7070\u98DE\u70DF\u706D\u3002\u800C\u6740\u4EBA\u8005 Ezio \u6B64\u65F6\u6700\u60F3\u544A\u8BC9\u5BF9\u65B9\u7684\uFF0C\u5C31\u662F\u5BF9\u65B9\u6740\u622E\u7684\u539F\u7F6A\uFF0C\u4E0D\u662F\u5176\u4ED6\u3002Ezio \u6B64\u65F6\u7684\u773C\u795E\u4E0D\u662F\u950B\u5229\u7684\uFF0C\u800C\u662F\u575A\u5B9A\u5982\u78D0\u77F3\uFF0C\u6211\u60F3\u5230\u8FD9\u4E00\u6B65\uFF0C\u5934\u53D1\u7070\u767D\u7684 Ezio \u662F\u53C2\u900F\u4E86\u4FE1\u6761\u4F55\u8C13\u201C\u4E07\u4E8B\u7686\u5141\u201D\u3002Ezio \u5F02\u5E38\u4ECE\u5BB9\u4E0E\u575A\u5B9A\uFF0C\u4ED6\u77E5\u9053\u4ED6\u7684\u6740\u622E\u5FC5\u5C06\u4F1A\u5E26\u6765\u6551\u8D4E\uFF0C\u8FD9\u662F\u4E0A\u5929\u7684\u5141\u8BB8\uFF0C\u4E5F\u662F\u5185\u5FC3\u7684\u667A\u6167\u3002\u5200\u950B\u4F7F\u5904\uFF0C\u5B57\u53E5\u6E83\u9003\uFF0C\u4EFB\u4F55\u7684\u9053\u7406\u90FD\u65E0\u6CD5\u963B\u6B62\u624B\u4E2D\u5C06\u901D\u7684\u7075\u9B42\u3002 <img src="'+n+'" alt="\u5E74\u8FC8\u7684Ezio"></p><p>\u5230\u4E86\u8FD9\u4E2A\u65F6\u5019\uFF0C\u624D\u77E5\u9053\u5E74\u8FC7\u534A\u767E\u7684 Ezio \u4E3A\u4EC0\u4E48\u5728\u541B\u58EB\u5766\u4E01\u5821\u53D7\u8FD9\u4E48\u591A\u4EBA\u7684\u5C0A\u91CD\uFF0C\u624D\u77E5\u9053\u4E3A\u4EC0\u4E48\u4ED6\u4E25\u5389\u6307\u8D23\u56E0\u9C81\u83BD\u800C\u9519\u6740\u4ED6\u4EBA\u7684\u5F92\u5F1F\uFF0C\u4E0D\u5168\u662F\u56E0\u4E3A\u9AD8\u660E\u7684\u523A\u6740\u827A\u672F\uFF0C\u66F4\u591A\u7684\u662F\u4ED6\u6BD4\u66F4\u591A\u5E74\u8F7B\u4EBA\u77E5\u9053\u5185\u5FC3\u4E00\u4E2A\u5141\u5B57\u7684\u5B9A\u593A\u3002\u800C\u6700\u540E\uFF0CEzio \u5B8C\u6210\u4E86\u5BF9\u81EA\u5DF1\u7684\u6551\u8D4E\uFF0C\u82F1\u96C4\u8FDF\u66AE\uFF0C\u5378\u7532\u5F52\u7530\u3002\u4E00\u9897\u98A0\u7C38\u7684\u5FC3\uFF0C\u7EC8\u4E8E\u6CE1\u5728\u6751\u706F\u72D7\u5420\u4E4B\u4E2D\u3002\u5728\u5B9A\u593A\u4E86\u8FD9\u4E48\u591A\u4EBA\u7684\u547D\u8FD0\u4E4B\u540E\uFF0C\u4ED6\u7EC8\u4E8E\u60F3\u5230\u4E86\u81EA\u5DF1\uFF0C\u60F3\u5230\u4E86 Sophia\uFF0C\u60F3\u5230\u4E86\u4F5B\u7F57\u4F26\u8428\u3002\u5982\u679C\u4E16\u4E0A\u6709\u4E00\u4E2A\u4EBA\u80FD\u963B\u6B62\u4ED6\uFF0C\u90A3\u8FD9\u4E2A\u4EBA\u80AF\u5B9A\u662F Ezio \u81EA\u5DF1\uFF0C\u56E0\u800C\u4ED6\u5141\u8BB8\u4E86\u82F1\u96C4\u7684\u79BB\u53BB\uFF0C\u4ECE\u5BB9\u7684\u5378\u4E0B\u8896\u5251\uFF0C\u7559\u4E0B\u80CC\u5F71\uFF0C\u81EA\u6B64\uFF0C\u6C38\u4E0D\u56DE\u5934\u3002\u800C\u95E8\u524D\u7684\u82B1\u4E0E\u9633\u5149\u5728\u5C81\u6708\u4E2D\u5077\u5077\u7A9C\u51FA\uFF0C\u4F3C\u4E4E\u7B49\u4E86\u4ED6\u51E0\u5341\u5E74\u3002</p><p>Ezio \u6700\u540E\u7684\u4FE1\uFF1A</p><blockquote><p>When I was a young man,<br> \u5F53\u6211\u8FD8\u662F\u4E2A\u5E74\u8F7B\u7684\u5C0F\u4F19\u5B50<br> I had liberty, but I did not see it.<br> \u6211\u62E5\u6709\u81EA\u7531\uFF0C\u5374\u4ECE\u672A\u8BA4\u77E5<br> I had time, but I did not know it.<br> \u6211\u62E5\u6709\u65F6\u95F4\uFF0C\u5374\u6BEB\u4E0D\u77E5\u60C5<br> And I had love, but I did not feel it.<br> \u800C\u6211\u4EA6\u62E5\u6709\u7231\uFF0C\u5374\u4ECE\u672A\u611F\u89C9\u5230<br> Many decades would pass before I understood the meaning of all three.<br> \u76F4\u5230\u6570\u5341\u5E74\u8FC7\u53BB\u540E\uFF0C\u6211\u624D\u771F\u6B63\u7406\u89E3\u8FD9\u4E09\u9879\u7684\u610F\u4E49<br> And now, the twilight of my life, misunderstanding has past into contentment.<br> \u800C\u73B0\u5728\uFF0C\u5782\u66AE\u4E4B\u5E74\u7684\u6211\uFF0C\u8FD9\u9886\u609F\u8BA9\u6211\u611F\u5230\u6EE1\u8DB3<br> Love, liberty, and time: once was so disposable, are the fuels that drive me forward.<br> \u66FE\u7ECF\u80FD\u81EA\u7531\u652F\u914D\u7684\u7231\uFF0C\u81EA\u7531\u4EE5\u53CA\u65F6\u95F4\uFF0C\u662F\u652F\u6301\u6211\u524D\u8FDB\u7684\u52A8\u529B<br> And love, most especially, mia caro.<br> \u800C\u7231\uFF0C\u662F\u6700\u7279\u522B\u7684\u4E00\u4E2A\uFF0C \u4EB2\u7231\u7684<br> For you, our children, our brothers and sisters.<br> \u4E3A\u4F60\uFF0C\u4E3A\u6211\u4EEC\u7684\u5B69\u5B50\u4EE5\u53CA\u4E3A\u6211\u4EEC\u7684\u5144\u5F1F\u59D0\u59B9\u4EEC<br> And for the vast and wonderful world that gives us lives and keeps us guessing,<br> \u4E3A\u8FD9\u8D50\u4E88\u6211\u4EEC\u751F\u547D\u4E0E\u60CA\u5947\u7684\u5E7F\u9614\uFF0C\u7CBE\u5F69\u7684\u4E16\u754C<br> endless affection\uFF0Cmio Sofia,<br> \u81F3\u6B7B\u4E0D\u6E1D\u7684\u631A\u7231\uFF0C \u6211\u7684\u7D22\u83F2\u4E9A<br> Forever yours, Ezio Auditore<br> \u6C38\u8FDC\u5C5E\u4E8E\u4F60\u7684\uFF0C Ezio Auditore</p></blockquote>',20),c=[l];function h(b,g){return o(),r("div",null,c)}var u=e(p,[["render",h],["__file","assassins-creed.html.vue"]]);export{u as default};
